INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,11,'<h2 class=\"sb_title\">Need Help?</h2>\n<p>We designed the steps below to help you get oriented with Music Say.So. If you find you need additional help, feel free to <a href=\"mailto:music@say.so\">contact us</a>.</p>\n<div class=\"sb_tabs\">\n	<ul>\n		<li><a href=\"#onboard_tabs_1\">Welcome!</a></li>\n		<li><a href=\"#onboard_tabs_2\">What Can I Do?</a></li>\n		<li><a href=\"#onboard_tabs_3\">What Do I Get?</a></li>\n		<li><a href=\"#onboard_tabs_4\">Get Started</a></li>\n		<li style=\"display:none;\"><a href=\"#onboard_tabs_5\"> </a></li>\n	</ul>\n\n	<div id=\"onboard_tabs_1\" class=\"sb_tabPane\">\n		<h3 class=\"sb_title\" align=\"center\" style=\"font-weight: bold; margin-bottom: 10px\">Congratulations and Thank You!</h3>\n		<h3>If you find you need additional help, feel free to <a href=\"mailto:music@say.so\">contact us</a>.</h3>\n		<p align=\"center\"><big>By becoming a member of Music Say.So, you\'ve achieved the level \"FAN\", and earned extra Music SaySo and PaySos.</big></p>\n		<br />\n		<ul class=\"sb_lineList\">\n\n			<li class=\"sb_img_experience\"><strong class=\"sb_theme_textHighlight\">\'Music SaySo\'</strong> is the currency of influence. You get SaySo by sharing your expertise, experience and opinion, and your Music SaySo determines your access to increasingly important levels of access and influence.</li>\n\n			<li class=\"sb_img_redeemable\"><strong class=\"sb_theme_textHighlight\">\'Music PaySos</strong> are almost as good as cash. Virtually everything you do earns you PaySos, which can be redeemed for all manner of goodies from Say.So. (<a class=\"sb_nav_element\" rel=\"sb_popBox_rewards\" href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/rewards\">see the Rewards Center</a>)</li>\n		</ul>\n	</div><!-- #tab_1 -->\n	<div id=\"onboard_tabs_2\" class=\"sb_tabPane\">\n		<h3 class=\"sb_title\" align=\"center\">Give Your Opinion, Share It and Earn!</h3>\n		<p align=\"center\">Your point of view and opinion are invaluable. Express yourself through fun polls and surveys, and earn Music SaySo and PaySos by rating music videos.</p>\n		<ul class=\"sb_lineList\">\n			<li class=\"sb_img_polls\"><strong class=\"sb_theme_textHighlight\">Polls</strong> are quick hits of opinion we\'d like you to share. Answer as many or as few as you\'d like, and for each one we\'ll drop a little something in your Music Say.So wallet.</li>\n			<li class=\"sb_img_surveys\"><strong class=\"sb_theme_textHighlight\">Surveys</strong> are the big money opportunities. These are important efforts to gauge how we are doing, and we\'ll make sure you are compensated accordingly.</li>\n			<li class=\"sb_img_quizzes\"><strong class=\"sb_theme_textHighlight\">Spotlight -</strong> Rate music videos and other content from signed and indie artists. Share them on Facebook or Twitter, we\'ll compensate you with SaySo and PaySos. No purchase necessary. </li>\n		</ul>\n	</div><!-- #tab_2 -->\n	<div id=\"onboard_tabs_3\" class=\"sb_tabPane\">\n		<h3 class=\"sb_title\" align=\"center\">Here\'s What You Get.</h3>\n		<p align=\"center\">Give us your opinion on classic and trending music and you\'ll earn Music PaySos, virtual currency redeemable for prizes and opportunities.</p>\n		<ul class=\"sb_lineList\">\n			<li class=\"sb_img_prizes\"><strong class=\"sb_theme_textHighlight\">Great Prizes.</strong> Almost everything you do as a Music Say.So member earns you PaySos. Use these to redeem prizes from the Rewards Center.<br /></li>\n			<li class=\"sb_img_giveaways\"><strong class=\"sb_theme_textHighlight\">A Chance To Win Prize Giveaways.</strong> Redeem your PaySos for entry tokens for our giveaways. Buy as many entry tokens for each giveaway as you want and increase your odds of winning.</li>\n		</ul>\n	</div><!-- #tab_3 -->\n	<div id=\"onboard_tabs_4\" class=\"sb_tabPane\">\n		<h3 class=\"sb_title\" align=\"center\">Okay, Let\'s Get Started!</h3>\n		<p align=\"center\" style=\"margin-top: 15px;\"><a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/embed-survey?survey_id=%CONFIG_PROFILE_SURVEY_ID%\" class=\"sb_nav_element\" rel=\"sb_popBox_surveys_hg\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_profile-survey.png\" alt=\"Take the Profile Survey\" /></a><br /><strong>Completing the Profile Survey takes a minute, but we will reward you with a hefty number of Music PaySos and grant you automatic access to our giveaways.</strong></p>\n		<br />\n		<br />\n		<h2 align=\"center\">Not ready to take the Profile Survey?<br />Check out some quick-hit activities.</h2>\n		<p align=\"center\" style=\"margin-top: 10px;\">\n			<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/polls\" class=\"sb_nav_element\" rel=\"sb_popBox_polls\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_polls.png\" alt=\"Polls\" style=\"margin: 0px 10px;\" /></a>\n			<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/surveys\" class=\"sb_nav_element\" rel=\"sb_popBox_surveys\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_surveys.png\" alt=\"Surveys\" style=\"margin: 0px 10px;\" /></a>\n			<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/spotlight\" class=\"sb_nav_element\" rel=\"sb_popBox_spotlight\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_trailers.png\" alt=\"Trailers\" style=\"margin: 0px 10px;\" /></a>\n		</p>\n	</div><!-- #tab_4 -->\n	<div id=\"onboard_tabs_5\" class=\"sb_tabPane\">\n		<p>\n			<label><strong>Name</strong></label> <input type=\"text\" />\n		</p>\n		<p>\n			<label><strong>Email</strong></label> <input type=\"text\" />\n		</p>\n		<p>\n			<label><strong>Question</strong></label> <textarea rows=\"5\"></textarea>\n		</p>\n		<p>\n			<input type=\"submit\" class=\"sb_theme_button\" value=\"Send\" />\n		</p>\n	</div><!-- #tab_5 -->\n</div><!-- .sb_tabs -->\n');
INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,12,'<h2 class=\"sb_title\">FAQ</h2>\n<p>\n	<strong>What is Music Say.So?</strong><br />\n	Music Say.So is a way to get rewarded for your opinions on music.  Members gain \"<strong class=\"sb_theme_textHighlight\"><big>SaySo</big></strong>\" (experience points) and \"<strong class=\"sb_theme_textHighlight\"><big>Music PaySos</big></strong>\" (currency). <strong class=\"sb_theme_textHighlight\"><big>PaySos</big></strong> can be redeemed for rewards and \"Entry Tokens\" into Music Say.So giveaways.\n	<a href=\"http://bit.ly/YyTFyj\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Do I have to pay for anything? Is there any shipping & handling fees for prizes obtained or won?</strong><br />\n	No, this is completely free to you. The only thing we want you to do is participate.\n	<a href=\"http://bit.ly/YG9s8p\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What is </strong><strong class=\"sb_theme_textHighlight\"><big>Music SaySo</big></strong><strong> used for?</strong><br />\n	Music SaySo determines your level of influence and involvement. Gaining Music SaySo will increase your Music Say.So level. <a href=\"http://bit.ly/10Le1Cc\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What are </strong><strong class=\"sb_theme_textHighlight\"><big>Music PaySos</big></strong><strong> used for?</strong><br />\n	PaySos are the currency you earn by participating in polls, answering surveys, and sharing your ideas. In fact, virtually everything you do in Social Say.So will earn you PaySos. These PaySos can be exchanged for various prizes and Entry Tokens in the Reward Center. <a href=\"http://bit.ly/XEvacS\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What are Entry Tokens?</strong><br />\n	Entry Tokens are your way of entering prize giveaways. Users that have purchased an Entry Token with their PaySos will be entered to win the giveaway. To view current giveaways click on the giveaway section of the bar.<br />At the end of each giveaway period a prize will be given away to a user that has purchased an Entry Token for the giveaway.<br />You can buy as many Entry Tokens as you want to increase your chance of winning.\n	<a href=\"http://bit.ly/YdcAw3\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Do you really give away the prizes?</strong><br />\n	YES! Every prize that is won will be shipped within 4-6 weeks. Users and fans of Social Say.So will get the prizes for playing, not the CEO\'s nephew or cousin.<br />At the end of each giveaway we will send out an email to all the Social Say.So users, letting you know who the lucky player is.\n	<a href=\"http://bit.ly/ZUIlce\">(more...)</a>\n</p>\n<p>\n	<strong>How do I get my prize?</strong><br />\n	When you win a prize we will contact you by email to get the information and mailing address needed to send the prize to you.\n<a href=\"http://bit.ly/XEs9cz\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Are there any geographic restrictions for playing??</strong><br />\n	Yes, you must reside in the USA to redeem points for prizes. We cannot ship items out of the USA.\n	<a href=\"http://bit.ly/10g2Fnp\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Do I have to play every day?</strong><br />\n	No, you do not have to play every day, but the more you play and participate, the more PaySos you can earn to exchange for Entry Tokens and other prizes.\n	<a href=\"http://bit.ly/ZUJfph\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Can I stop playing at any time?</strong><br />\n	Yes, participate as much (or as little) as you want, but remember, the more you play the better your chances of winning cool prizes.\n	<a href=\"http://bit.ly/10borYm\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>How long do the surveys / games take to complete?</strong><br />\n	Usually between 5 and 15 minutes. Generally, the longer the survey is, the more PaySos you can earn.\n	<a href=\"http://bit.ly/YCCcSS\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What if I stop a survey in the middle of taking it? Do I still get credit? Do I have to restart it?</strong><br />\n	Once you start a survey you must complete it in order to earn your PaySos.\n	<a href=\"http://bit.ly/YddbOy\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>How often can I get SaySo/PaySos for sharing?</strong><br />\n	For details on sharing, click here\n	<a href=\"http://bit.ly/YRUwDB\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What are you doing with the information you are collecting?</strong><br />\n	We use the opinions and information you give us to (a) provide your collective say-so to music labels, independent artists and advertising companies to help inform their creative ideas for marketing and other music related content; and (b) provide say.so members with a better Music Say.So experience!\n	<a href=\"http://bit.ly/11IcNXs\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Can I invite other people to play?</strong><br />\n	Yes, you can invite friends through Facebook or Twitter. You can also invite friends using our \"Send To A Friend\"Â feature found in the settings section of the bar. You can earn additional PaySos by getting your friends to play.\n	<a href=\"http://bit.ly/ZoN6HK\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Can I trade my prize for another prize?</strong><br />\n	No, the prize you win or redeem is the prize you get.\n	<a href=\"http://bit.ly/10g2qIZ\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Can I track the status of the orders I have redeemed/won?</strong><br />\n	You will be sent a confirmation of your redemption or win. The delivery time of your item depends on the type of item you have redeemed or won. If you feel like there is a problem with your order, please contact us at music@say.so.\n	<a href=\"http://bit.ly/16VZCWK\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>What is the Promotion Period</strong><br />\n	<a href=\"http://bit.ly/X9URpU\" target=\"_blank\">Click here</a> for detailed information on current, planned and previous giveaway information.\n	<a href=\"http://bit.ly/X9URpU\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>How do you select the winner for drawings?</strong><br />\n	You must redeem your Music PaySos for Entry Tokens to be entered in the giveaways. Winners will be chosen at random from all entrants.\n	<a href=\"http://bit.ly/YyVHyn\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>I am missing Music SaySo or PaySos!</strong><br />\n	For missing SaySo or PaySos\n	<a href=\"http://bit.ly/10LfyIu\" target=\"_blank\">click here</a>\n</p>\n<p>\n	<strong>I have a question that is not included in this FAQ!</strong><br />\n	You can check <a href=\"http://bit.ly/10boHq6\" target=\"_blank\">HERE</a> to see if there is an answer to a question that is not included in this FAQ. If you don\'t see your question or answer, please feel free to send your question using the feedback section.\n</p>\n<p>\n	<strong>How do I contact someone?</strong><br />\n	We value our users feedback, concerns, and suggestions and want to hear from you. You can contact us through the feedback section of the bar, or by emailing us directly at music@say.so. You can also use http://www.twitter.com/saysoapp or http://www.facebook.com/musicsayso to get in touch or send feedback.\n	<a href=\"http://bit.ly/10g2yIC\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>Where can I find the official Reward and Giveaway rules?</strong><br />\n	Links to the current Rules, Terms and Conditions, and Privacy Policies are all located at the bottom of the \'Edit Profile\' tab on the bar.\n	<a href=\"http://bit.ly/11Id3FM\" target=\"_blank\">(more...)</a>\n</p>\n<p>\n	<strong>How do I temporarily disable, or uninstall the Music Say.So app?</strong><br />\n	Once registered, your account, Music PaySos and content are always waiting for you, whether you have the app installed or not; but modern browsers are pretty slick now in letting us turn apps on and off without uninstalling them. You can leave Music Say.So installed, and only activate it when you need it, or want to take advantage of a Social Say.So offer, then deactivate it after you\'re done. No more uninstalling and reinstalling! <a href=\"http://bit.ly/X9V4JA\" target=\"_blank\">Click here</a> for further instructions on how to disable this app.\n</p>\n<p>\n	<strong>How do I remove the Music Say.So browser extension?</strong><br />\n	If you want to remove the app from your browser, here are instructions for each major browser type.  If you decide later to reinstall, use your original registration email and your PaySos, level progress and earning will be there where you left off! For removal instructions\n	<a href=\"http://bit.ly/ZrAq6p\" target=\"_blank\">click here</a>.\n</p>\n<p>\n	<strong>How do I link my Facebook or Twitter account?</strong><br />\n	You can check <a href=\"http://bit.ly/YCClpk\" target=\"_blank\">HERE</a> for instructions on how to link your Facebook / Twitter account, or see below.\n</p>\n<ol>\n	<li>Click \"Edit Profile\"</li>\n	<li>Click to link your Twitter or Facebook account.</li>\n	<li>Allow access in order for the accounts to be linked.</li>\n</ol>\n<p>\n	<strong>How do I disconnect my Facebook / Twitter account from the Say.So bar?</strong><br />\n	You can check <a href=\"http://bit.ly/14m6IUF\" target=\"_blank\">HERE</a> for instructions on how to unlink your Facebook / Twitter account, or see below.\n</p>\n<p>\n	<strong>Facebook:</strong><br />\n</p>\n<ol>\n	<li>Log into Facebook.</li>\n	<li>Click on <em>Home</em> > <em>Account Settings.</em></li>\n	<li>Click on <em>Apps</em>. You will see <em>Say.So app</em> listed here.</li>\n	<li>Click on the app and then <em>Remove App</em> in the top right area of the application info box.</li>\n</ol>\n<p>\n	<strong>Twitter:</strong><br />\n</p>\n<ol>\n	<li>Log into Twitter.</li>\n	<li>Click on your profile icon (top right part of your screen) > <em>Settings</em>.</li>\n	<li>Open the <em>Applications</em> tab and you will see the <em>Say.So app</em> listed here.</li>\n	<li>Click on <em>Revoke Access</em> to remove the app.</li>\n</ol>\n<br /><br />\n');
INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,13,'<h2 class=\"sb_title\">Feedback</h2>\n<div id=\"sb_feedback_container\"></div>\n<script type=\"text/javascript\" charset=\"utf-8\">\n	$SQ.getScript(\"//s3.amazonaws.com/getsatisfaction.com/javascripts/feedback-v2.js\", function() {\n		sayso.evalInPageContext(function() {\n			new GSFN.feedback_widget({\n				display : \"inline\",\n				container : \"sb_feedback_container\",\n				company : \"socialsayso\",\n				style : \"question\",\n				product : \"socialsayso_social_sayso\",\n				limit : \"5\",\n				width : \"520px\",\n				height : \"450px\"\n			});\n		});\n	});\n</script>\n');
