INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,37,'<h2 class=\"sb_title\" align=\"center\">Okay, Let\'s Get Started!</h2>\n<p align=\"center\" style=\"margin-top: 15px;\"><a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/embed-survey?survey_id=%CONFIG_PROFILE_SURVEY_ID%\" class=\"sb_nav_element\" rel=\"sb_popBox_surveys_hg\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_profile-survey.png\" alt=\"Take the Profile Survey\" /></a><br /><strong>Completing the Profile Survey takes a minute, but we will reward you with a hefty amount of PaySos and grant you automatic access to our giveaways.</strong></p>\n<br />\n<br />\n<h2 align=\"center\">Not ready to take the Profile Survey yet?  <br />Check out some quick-hit activities.</h2>\n<p align=\"center\" style=\"margin-top: 10px;\">\n	<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/polls\" class=\"sb_nav_element\" rel=\"sb_popBox_polls\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_polls.png\" alt=\"Polls\" style=\"margin: 0px 10px;\" /></a>\n	<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/surveys\" class=\"sb_nav_element\" rel=\"sb_popBox_surveys\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_surveys.png\" alt=\"Surveys\" style=\"margin: 0px 10px;\" /></a>\n	<a href=\"//%CONFIG_BASE_DOMAIN%/starbar/%CONFIG_SHORT_TITLE%/spotlight\" class=\"sb_nav_element\" rel=\"sb_popBox_spotlight\"><img src=\"//%CONFIG_BASE_DOMAIN%/images/music/img_badge_quizzes.png\" alt=\"Spotlight\" style=\"margin: 0px 10px;\" /></a>\n</p>\n');
INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,38,'<h2 class=\"sb_title\" align=\"center\">Here\'s What You Get.</h2>\n<p align=\"center\"><big>Help us better serve your Music needs and you\'ll earn PaySos, virtual currency redeemable for prizes and opportunities.</big></p>\n<br />\n<ul class=\"sb_lineList\">\n	<li class=\"sb_img_prizes\"><strong class=\"sb_theme_textHighlight\">Great Prizes.</strong> Almost everything you do as a Say.So music buff earns you PaySos. Use these to redeem prizes from the Rewards Center.</li>\n	<li class=\"sb_img_giveaways\"><strong class=\"sb_theme_textHighlight\">A Chance To Win Prize Giveaways. </strong> Redeem your PaySos for entry tokens for our giveaways. Buy as many entry tokens for each giveaway as you want and increase your odds of winning.</li>\n</ul>\n');
INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,39,'<h2 class=\"sb_title\" align=\"center\">Give Your Opinion, Share It and Earn!</h2>\n<p align=\"center\"><big>Your point of view and opinion are invaluable. Express yourself through fun polls and surveys, and earn points by rating videos.</big></p>\n<br />\n<ul class=\"sb_lineList\">\n	<li class=\"sb_img_polls\"><strong class=\"sb_theme_textHighlight\">Polls</strong> are quick hits of opinion we\'d like you to share. Answer as many or as few as you\'d like, and for each one we\'ll drop a little something in your Say.so wallet.</li>\n	<li class=\"sb_img_surveys\"><strong class=\"sb_theme_textHighlight\">Surveys</strong> are the big money opportunities. These are important efforts to gauge how we are doing, and we\'ll make sure you are compensated accordingly.</li>\n	<li class=\"sb_img_quizzes\"><strong class=\"sb_theme_textHighlight\">Spotlight - </strong> Rate music videos and other content from signed and indie artists. Share them on Facebook or Twitter, we\'ll compensate you with SaySo and PaySos. No purchase necessary. \n</li>\n</ul>\n');
INSERT INTO `starbar_content` (`starbar_id`,`starbar_content_key_id`,`content`) VALUES (6,40,'<h2 class=\"sb_title\" align=\"center\">Welcome!</h2>\n<h3>We designed the steps below to help you get oriented with Music Say.So. If you find you need additional help, feel free to <a href=\"mailto:music@say.so\">contact us</a>.</h3>\n<p align=\"center\"><big>By becoming a member of Music Say.So, you\'ve achieved the level \"FAN\", and earned extra SaySo and PaySos.</big></p>\n<br />\n<ul class=\"sb_lineList\">\n	<li class=\"sb_img_experience\"><strong class=\"sb_theme_textHighlight\">\'Music SaySo\'</strong> is the currency of influence. You get SaySo by sharing your expertise, experience and opinion, and your Music SaySo determines your access to increasingly important levels of access and influence.</li>\n	<li class=\"sb_img_redeemable\"><strong class=\"sb_theme_textHighlight\">\'Music PaySos\'</strong> are almost as good as cash. Virtually everything you do earns you Music PaySos, which can be redeemed for all manner of goodies from Say.So. (<a class=\"sb_nav_element\" rel=\"sb_popBox_rewards\" href=\"/starbar/%CONFIG_SHORT_TITLE%/rewards\">see the Rewards Center</a>)</li>\n</ul>\n');
